# Function to rename columns
rename_columns = function(df) {
  df %>%
    rename_with(~ new_col_names[.x], .cols = intersect(names(df), names(new_col_names)))
}

# Get all dataframes in the environment and rename their columns
df_names = ls()
for (df_name in df_names) {
  if (inherits(get(df_name), "data.frame")) {  # Check if it's a dataframe
    assign(df_name, rename_columns(get(df_name)), envir = .GlobalEnv)
  }
}

